import React, { useState } from "react";
import "./EducationSection.css"

const EducationSection = () => {
  const [educationEntries, setEducationEntries] = useState([
    {
      institution: "",
      levelofeducation: "",
      majorsMinors: "",
      graduationDate: "",
    },
  ]);

  const handleInputChange = (index, field, value) => {
    const newEntries = [...educationEntries];
    newEntries[index][field] = value;
    setEducationEntries(newEntries);
  };

  const handleAddEducation = () => {
    setEducationEntries([
      ...educationEntries,
      {
        institution: "",
        levelofeducation: "",
        majorsMinors: "",
        graduationDate: "",
      },
    ]);
  };

  const handleDeleteEducation = (index) => {
    const newEntries = [...educationEntries];
    newEntries.splice(index, 1);
    setEducationEntries(newEntries);
  };

  return (
    <div className="education-container">
      <h2 className="education-header">Education</h2>
      <div className="education-form-container">
        {educationEntries.map((entry, index) => (
          <div key={index} className="education-form-entry">
            <input
              type="text"
              placeholder="Institution"
              value={entry.institution}
              onChange={(e) => handleInputChange(index, "institution", e.target.value)}
            />
            <input
              type="text"
              placeholder="Level of education"
              value={entry.levelofeducation}
              onChange={(e) => handleInputChange(index, "levelofeducation", e.target.value)}
            />
            <input
              type="text"
              placeholder="Majors/Minors"
              value={entry.majorsMinors}
              onChange={(e) => handleInputChange(index, "majorsMinors", e.target.value)}
            />
            <input
              type="date"
              placeholder="Graduation Date"
              value={entry.graduationDate}
              onChange={(e) => handleInputChange(index, "graduationDate", e.target.value)}
            />
            <button onClick={() => handleDeleteEducation(index)}>Delete</button>
          </div>
        ))}
        <button onClick={handleAddEducation}>Add Education</button>
      </div>
    </div>
  );
};

export default EducationSection;
